mapper.update:
update day_good
id=#{id}, company_code=#{companyCode}, price=#{price}, rate=#{rate}, main_money=#{mainMoney}, main_rate=#{mainRate}, chaoda_money=#{chaodaMoney}, chaoda_rate=#{chaodaRate}, da_money=#{daMoney}, da_rate=#{daRate}, date=#{date}, time=#{time}, original_time=#{originalTime}
id=#{id}

mapper.delete:
delete from day_good
where id in

  #{idItem}

delete from day_good
where id in
#{item.id}

mapper.select:
select * from day_good


    AND id=#{id}


    AND company_code=#{companyCode}


    AND price=#{price}


    AND rate=#{rate}


    AND main_money=#{mainMoney}


    AND main_rate=#{mainRate}


    AND chaoda_money=#{chaodaMoney}


    AND chaoda_rate=#{chaodaRate}


    AND da_money=#{daMoney}


    AND da_rate=#{daRate}


    AND date=#{date}


    AND time=#{time}


    AND original_time=#{originalTime}

select * from day_good


    AND id=#{id}


    AND company_code=#{companyCode}


    AND price=#{price}


    AND rate=#{rate}


    AND main_money=#{mainMoney}


    AND main_rate=#{mainRate}


    AND chaoda_money=#{chaodaMoney}


    AND chaoda_rate=#{chaodaRate}


    AND da_money=#{daMoney}


    AND da_rate=#{daRate}


    AND date=#{date}


    AND time=#{time}


    AND original_time=#{originalTime}


limit #{startIndex},#{limit}

select count(*) from day_good


    AND id=#{id}


    AND company_code=#{companyCode}


    AND price=#{price}


    AND rate=#{rate}


    AND main_money=#{mainMoney}


    AND main_rate=#{mainRate}


    AND chaoda_money=#{chaodaMoney}


    AND chaoda_rate=#{chaodaRate}


    AND da_money=#{daMoney}


    AND da_rate=#{daRate}


    AND date=#{date}


    AND time=#{time}


    AND original_time=#{originalTime}

select g.*,g.main_money/g1.main_money as preMainMoneyRate, g.main_money-g1.main_money as preMainMoneyCha, c.name as company_name,c.type as company_type,c.market_worth,


    g1.main_money as preTimeMainMoney1,g1.rate as preTimeRate1,case when g2.main_money is null then 0 ELSE g1.main_money/g2.main_money END as preMainMoneyRate1,g1.main_money-g2.main_money as preMainMoneyCha1,


    '0' as preTimeMainMoney1,'0' as preTimeRate1,'0' as preMainMoneyRate1,




    g2.main_money as preTimeMainMoney2,g2.rate as preTimeRate2,


    '0' as preTimeMainMoney2,'0' as preTimeRate2,




    v1.rate as preDayRate1,


    '0' as preDayRate1,




    v2.rate as preDayRate2


    '0' as preDayRate2


from day_good g
left JOIN company c on g.company_code=c.code

  LEFT JOIN (select * FROM day_value where `date`=#{preDayDate1}) v1 ON g.company_code=v1.company_code


  LEFT JOIN (select * FROM day_value where `date`=#{preDayDate2}) v2 ON g.company_code=v2.company_code


  LEFT JOIN (select * FROM day_good where `time`=#{preTime1}  AND date=#{date}) g1 ON g.company_code=g1.company_code


  LEFT JOIN (select * FROM day_good where `time`=#{preTime2}  AND date=#{date}) g2 ON g.company_code=g2.company_code



    AND g.company_code=#{companyCode}


    AND g.price <=#{price}


    AND g.rate <=#{rate}


    AND g.main_money >=#{mainMoney}


    AND g.main_rate >=#{mainRate}


    AND g.chaoda_money=#{chaodaMoney}


    AND g.chaoda_rate=#{chaodaRate}


    AND g.date=#{date}


    AND g.time=#{time}


    and v1.rate <=#{preDayDate1}


    and v2.rate <=#{preDayDate2}


    and c.type=#{type}


    and c.market_worth BETWEEN #{marketWorth1} and #{marketWorth2}

  order by g.main_money desc,g.price asc,g.rate desc
