mapper.update:
update day_value
id=#{id}, company_code=#{companyCode}, start_price=#{startPrice}, end_price=#{endPrice}, rate=#{rate}, total_money=#{totalMoney}, huan_shou=#{huanShou}, date=#{date}, create_time=#{createTime}
id=#{id}

mapper.delete:
delete from day_value
where id in
#{idItem}

delete from day_value
where id in
#{item.id}

