	AND id=#{id}


	AND company_code=#{companyCode}


	AND start_price=#{startPrice}


	AND end_price=#{endPrice}


	AND rate=#{rate}


	AND total_money=#{totalMoney}


	AND huan_shou=#{huanShou}


	AND date=#{date}


	AND create_time=#{createTime}

select * from day_value


	AND id=#{id}


	AND company_code=#{companyCode}


	AND start_price=#{startPrice}


	AND end_price=#{endPrice}


	AND rate=#{rate}


	AND total_money=#{totalMoney}


	AND huan_shou=#{huanShou}


	AND date=#{date}


	AND create_time=#{createTime}


	limit #{startIndex},#{limit}

