mapper.update:
update day_value
id=#{id}, company_code=#{companyCode}, start_price=#{startPrice}, end_price=#{endPrice}, rate=#{rate}, total_money=#{totalMoney}, date=#{date}, create_time=#{createTime}
id=#{id}

mapper.delete:
delete from day_value
where id in
#{idItem}

delete from day_value
where id in
#{item.id}

mapper.select:
select * from day_value


        AND id=#{id}


        AND company_code=#{companyCode}


        AND start_price=#{startPrice}


        AND end_price=#{endPrice}


        AND rate=#{rate}


        AND total_money=#{totalMoney}


        AND date=#{date}


        AND create_time=#{createTime}

select * from day_value


        AND id=#{id}


        AND company_code=#{companyCode}


        AND start_price=#{startPrice}


        AND end_price=#{endPrice}


        AND rate=#{rate}


        AND total_money=#{totalMoney}



        AND date=#{date}



        AND create_time=#{createTime}



order by date desc limit #{startIndex},#{limit}

select count(*) from day_value


        AND id=#{id}


        AND company_code=#{companyCode}


        AND start_price=#{startPrice}


        AND end_price=#{endPrice}


        AND rate=#{rate}


        AND total_money=#{totalMoney}


        AND date=#{date}


        AND create_time=#{createTime}
